declare type Step = {
    code: string;
    file: string | undefined;
    lang: string;
    focus: string | null;
};
declare type StepTransition = {
    prevCode: string | null;
    prevFocus: string | null;
    nextCode: string | null;
    nextFocus: string | null;
    file: string | undefined;
    lang: string;
};
export { useForwardTransitions, useBackwardTransitions };
declare function useForwardTransitions(steps: Step[]): StepTransition[];
declare function useBackwardTransitions(steps: Step[]): StepTransition[];
