import React from "react";
import { Snapshot } from "./editor-frame";
import { CodeAnnotation } from "@code-hike/smooth-code";
export { EditorStep, StepFile, useSnapshots };
declare type StepFile = {
    code: string;
    focus?: string;
    lang: string;
    name: string;
    annotations?: CodeAnnotation[];
};
declare type EditorPanel = {
    tabs: string[];
    active: string;
    heightRatio: number;
};
declare type EditorStep = {
    files: StepFile[];
    northPanel: EditorPanel;
    southPanel?: EditorPanel;
    terminal?: string;
};
declare function useSnapshots(ref: React.RefObject<HTMLDivElement>, prev: EditorStep, next: EditorStep): {
    prevSnapshot: Snapshot | null;
    nextSnapshot: Snapshot | null;
};
